var access_key=getUrlParameter("token"),form=$("#password-form");$(function(){form.submit(function(e){e.preventDefault();var s=$("#password").val(),r=$("#confirm_password").val();if(s==r){var o=$("#password-form").serialize();$.ajax({type:"POST",url:REMOTE_APPLICATION_URL+"/api/v2/user.php?action=password&token="+access_key,data:o,success:function(e){swal({title:"Good job!",text:"Password has successfully been changed.",icon:"success",timer:2e3,button:!1,closeOnClickOutside:!1}).then(function(){window.location.href="/account/settings?token="+access_key})},error:function(e){swal("Error Message","Internal Server Error","error")}})}else swal("Oooops!","Passwords do not match. Please check again.","error")})});