function deleteProduct(e,t){$.ajax({type:"DELETE",url:REMOTE_APPLICATION_URL+"/api/v2/product.php?id="+e+"&farmer_id="+t+"&token="+access_key,contentType:"text/plain",success:function(t){swal("Product Deleted","Product has successfully been deleted.","success"),$("#row_"+e).fadeOut()},error:function(e){swal("Ooops!","Deleting this product was not successful. Please try again.","error")}})}var access_key=getUrlParameter("token"),id=getUrlParameter("id");$(function(){$.ajax({type:"GET",url:REMOTE_APPLICATION_URL+"/api/v2/product.php",data:{token:access_key,farmer_id:id},success:function(e){var t=[];$.each(e,function(e,a){t.push("<tr id='row_"+a.id+"'><td class='text-center align-middle'>"+a.id+"</td><td class='text-center align-middle'><img src='"+REMOTE_APPLICATION_URL+"/assets/img/products/"+a.peopleid+"/"+a.id+".jpg' width='100px' /></td><td class='text-center align-middle large-text'>"+a.name+"</td><td class='text-center align-middle'><span class='badge bg-brown text-white larger-text'>"+a.currency+" "+a.price+" / "+a.unit.toUpperCase()+"</span></td><td class='text-center align-middle large-text'>"+a.location+"</td><td class='text-center align-middle large-text'>"+a.created_at+"</td><td class='text-center align-middle large-text'>"+a.updated_at+"</td><td class='text-center align-middle'><div class='btn-group'><a href='/product/view?id="+a.id+"&token="+access_key+"' class='btn btn-primary'><i class='fa fa-eye'></i></a><a href='/product/edit?id="+a.id+"&token="+access_key+"' class='btn btn-warning'><i class='fa fa-pencil'></i></a><a onclick='deleteProduct("+a.id+","+a.peopleid+")' class='btn btn-danger'><i class='fa fa-trash'></i></a></div></td></tr>")}),$("<tbody/>",{html:t.join("")}).appendTo("#productTable"),$("#productTable").DataTable({responsive:!0})}})});